import classes from './Cart.module.css';
import React,{useContext} from 'react';
import Modal from '../UI/Modal';
import Card from '../UI/Card';
import CartContext from '../context/cart-context';

const Cart = (props) =>{
	const ctx = useContext(CartContext);
	console.log(ctx.cartState.items);
	 const cartItems=<ul className={classes['cart-item']}>{ctx.cartState.items.map(m=><li>
		 <span>{m.name}</span>
		 <span>                      {m.amount}</span>
		 </li>
	)}</ul>
	return(
		<Card>
		<Modal onCloses={props.onClick} >
		{cartItems}
			<div className={classes.total}>
				<span>Total Amount</span>
				<span>$35.66</span>
				</div>
			<div className={classes.actions}>
				<button onClick = {props.onClick} className={classes['button--alt']}>close</button>
				<button className={classes.button}>order</button>
			</div>
	</Modal>
	</Card>
	)
}
export default Cart;
