import CartIcon  from '../Cart/CartIcon';
import classes from './HeaderCartButton.module.css';
import React , { useContext } from 'react';

import  CartContext  from '../context/cart-context';

const HeaderCartButton = () =>{
	const {setState} = useContext(CartContext);
	const cartCtx = useContext(CartContext);

	const numberOrder = cartCtx.item.reduce((cNumber,item)=>{
		return cNumber+ item.amount;
	},0);

	const buttonHandler = () =>{
		setState(true);
	}

	return(
		<button  onClick={buttonHandler} className={classes.button}>
			<span className={classes.icon}><CartIcon/></span>
			<span>Your Cart</span>
			<span className={classes.badge}>{numberOrder}</span>
		</button>
	)
}
export default HeaderCartButton;
