import CartIcon  from '../Cart/CartIcon';
import classes from './HeaderCartButton.module.css';
import React , { useContext } from 'react';

import  CartContext  from '../context/cart-context';

const HeaderCartButton = (props) =>{
	const Ctx = useContext(CartContext);
	console.log(cartCtx.cartContext.items);
	
	const numberOfOrder = Ctx.cartContext.items.reduce((curNumber,item) =>{
		return curNumber + item.amount;
       },0);
	console.log(numberOfOrder);

	return(
		<button onClick={props.onButton} className={classes.button}>
			<span className={classes.icon}><CartIcon/></span>
			<span>Your Cart</span>
			<span className={classes.badge}>{numberOfOrder}</span>
		</button>
	)
}
export default HeaderCartButton;
