import React ,{useState,useReducer} from 'react';
import CartContext from './cart-context';

const defaultCart={
	items:[],
	totalamount:0
} 
const cartReducer = (state,action)=>{
	if( action.type === 'Add' ){
		const updateItem =  state.items.concat(action.item)
		const updateTotalamount = state.totalamount +( action.item.amount * action.item.price);	
		return{
			items :updateItem,
			totalamount : +updateTotalamount,
		}
	}
	else if(action.type === 'REMOVE' )
	
	{
        const filterItem = state.items.filter(()=> !action.id);
        const filterTotalamount= state.totalamount - (action.item.amount * action.item.price )
		return{
			items:filterItem,
			totalamount: filterTotalamount,
		}
	}

return defaultCart
}
 const CartProvider = (props) =>{
	 const [cartState, dispatchAction] = useReducer(cartReducer,defaultCart)


	 const addItemCart = (item) => {
		 dispatchAction({type:'Add', item : item})

	 }
	 const removeItemCart = (id) =>{
		 dispatchAction({type:'REMOVE' , id:id})

	 }
	 const cartContext ={
		 items:[],
		 totalamount:0,
		 addItem:addItemCart,
		 removeItem:removeItemCart
	 }
	 
	 return <CartContext.Provider value={{cartContext,cartState}}>
		 {props.children}
	 </CartContext.Provider>
 }
 export default CartProvider;
