import React,{useEffect,useState} from 'react';
import classes from './Available.module.css';
import Card from '../UI/Card';
import MealsItem from './MealItems/MealsItem';
const [meals,setMeals] = useState();
useEffect(()=>{
	const FetchData = async() =>{
		const response = await fetch('https://foodapp-b49e1-default-rtdb.firebaseio.com/Meals.json');
		const responseData = await response.json();
		const loadedData = [];
		for(const key in responseData){
			loadedData.push({
				id:key,
				name:reponseData[key].name,
				price:reponseData[key].price,
				description:reponseData[key].description,
			})
			setMeals(loadedData);
		}
		FetchData();
	}

},[])
  const AvailableMeals = () =>{
	  const meals=	meals.map((meal)=>{
		return(  <MealsItem
		     id={meal.id}
		     key={meal.id}
			 name={meal.name}
			 description={meal.description}
			 price={meal.price}
		/>
	  
	)
	})
	
	  return(
		  
		  <section className={classes.meals}>
		  <Card>
		  {meals}
          </Card>
		  </section>
		 


	  )
  }
  export default AvailableMeals;
