import classes from './Cart.module.css';
import React,{useContext} from 'react';
import Modal from '../UI/Modal';
import CartContext from '../context/cart-context';
import CartItem from './CartItem';

const Cart = (props) =>{
	const ctx = useContext(CartContext);
	const totalamount = `$${ctx.cartState.totalamount}`; 
	const hasItem = ctx.cartState.items.length > 0;

	const cartAddHandler = (item) =>{
		console.log(item);
		ctx.cartContext.addItem(item);


	}
	const cartRemoveHandler = (id) =>{

	}

	 const cartItems=<ul className={classes['cart-items']}>{ctx.cartState.items.map(item=>
		<CartItem key={item.id}
		          id={item.id}
                  name={item.name}
				  price={item.price}
				  amount={item.amount}
				  onAdd={cartAddHandler.bind(null,item)}
				  onRemove={cartRemoveHandler.bind(null,item.id)}
				 
				  />)}</ul>
	return(
		
		<Modal onCloses={props.onClick} >
		{cartItems}
			<div className={classes.total}>
				<span>Total Amount</span>
				<span>{totalamount}</span>
				</div>
			<div className={classes.actions}>
				<button onClick = {props.onClick} className={classes['button--alt']}>close</button>
				{hasItem && <button className={classes.button}>order</button>}
			</div>
	</Modal>

	)
}
export default Cart;
