import classes from './Cart.module.css';
import React,{useContext} from 'react';
import Modal from '../UI/Modal';
import Card from '../UI/Card';
import CartContext from '../context/cart-context';

const Cart = (props) =>{
	const ctx = useContext(CartContext);
	const removeHandler = (e) =>{
		const v = (e.currentTarget.id);
		console.log(v);
		
		ctx.cartContext.removeItem(v);

	} 
	console.log(ctx.cartContext);
	 const cartItems=<ul className={classes['cart-item']}>{ctx.cartState.items.map(m=><li>
		 <span>{m.name}</span>
		 <span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
			 &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
			 &nbsp;&nbsp;&nbsp;&nbsp;{ m.amount}</span>
			 <span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
				 &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
				 &nbsp;&nbsp;&nbsp;&nbsp;
				 {`$${m.price}`}</span>
				 <span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
					 &nbsp;&nbsp;&nbsp;
					 &nbsp;&nbsp;&nbsp;&nbsp;
					 &nbsp;<button onClick={removeHandler}>remove</button></span>
		 </li>
	)}</ul>
	return(
		<Card>
		<Modal onCloses={props.onClick} >
		{cartItems}
			<div className={classes.total}>
				<span>Total Amount</span>
				<span>$35.66</span>
				</div>
			<div className={classes.actions}>
				<button onClick = {props.onClick} className={classes['button--alt']}>close</button>
				<button className={classes.button}>order</button>
			</div>
	</Modal>
	</Card>
	)
}
export default Cart;
